@isTest
public class AccountDescriptionBatchTest {
    @isTest
    static void testBatchExecution() {
        Account account = new Account(Name = 'Test Account');
        insert account;

        Test.startTest();
        AccountDescriptionBatch batch = new AccountDescriptionBatch();
        Database.executeBatch(batch);
        Test.stopTest();

        Account result = [SELECT Description FROM Account WHERE Id = :account.Id];
        Assert.areEqual('Processed', result.Description, 'Description should be updated to Processed');
    }
}

