@isTest
public class ContactCleanupBatchTest {
    @testSetup
    static void setupTestData() {
        List<Contact> contacts = new List<Contact>();
        for (Integer i = 0; i < 5; i++) {
            contacts.add(new Contact(LastName = 'Test Contact ' + i));
        }
        insert contacts;
    }

    @isTest
    static void testBatchCleansContacts() {
        Test.startTest();
        ContactCleanupBatch batch = new ContactCleanupBatch();
        Database.executeBatch(batch);
        Test.stopTest();

        List<Contact> results = [SELECT Description FROM Contact];
        for (Contact contact : results) {
            Assert.areEqual('Cleaned', contact.Description, 'Each Contact Description should be Cleaned');
        }
    }
}

